import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
    table: {
        width: '50%',
        margin:'auto',
      },
      TableRow:{
          borderRight:"1px solid #f0f0f6",
          borderLeft:"1px solid #f0f0f6",
      },
  });
  function createData() {
    return { };
  }
  
  const rows = [
    createData('Homework title','Subject','MA','20-12-2020','#e9ddfd','16.5'),
    createData('Homework title','Subject','FR','20-12-2020','#fceeca','13.8'),
    createData('Homework title','Subject','AR','20-12-2020','#daf0bc','13.5'),
    createData('Homework title','Subject','EN','20-12-2020','#fce3d5','17.00'),
    createData('Homework title','Subject','PH','20-12-2020','#f8d3ec','11.00'),
  ];
export default function Absence() {
    const classes = useStyles();
    return (
        <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
            <TableHead >
                <TableRow className={classes.TableRow}>
                    <TableCell style={{color:"rgba(21, 26, 59, 0.5)"}}>Homework title</TableCell>
                    <TableCell align="center" style={{color:"rgba(21, 26, 59, 0.5)"}}>Date</TableCell>
                    <TableCell align="right">Score</TableCell>
                </TableRow>
            </TableHead>
            <TableBody>
            {rows.map((row) => (
                <TableRow key={row.Title} className={classes.TableRow}>
                    <TableCell component="th" scope="row">
                        <div className={classes.Abrv} style={{backgroundColor:row.BgColor}}>
                            {row.Abrv}
                        </div>
                        <div className={classes.TS}>
                            <span>{row.Title}</span>
                            <div className={classes.Subject}>{row.Subject}</div>
                        </div>
                    </TableCell>
                    <TableCell align="center">{row.Date}</TableCell>
                    <TableCell align="right">{row.Score}</TableCell>
                </TableRow> 
            ))}
            </TableBody>
        </Table>
    </TableContainer>
    )
}
